mapper.select:
select ID, FROM_TIME, END_TIME, PRODUCT_ID, PRODUCT_CODE, TTL_SALE, TTL_AMOUNT, TTL_DEALS, TTL_ACCESS, CREATE_TIME, UPDATE_TIME, ROWNUM RN from T_PRODUCT_STATISTICS where 1= 1
AND PRODUCT_CODE = ${productCode}
AND FROM_TIME > #{fromTime}
AND END_TIME < #{endTime}
AND ROWNUM between ${page.start} and ${page.pageSize}

mapper.update:
    update T_PRODUCT_STATISTICS set

	    TTL_SALE=#{template.saleTotal},


	    TTL_AMOUNT=#{template.totalAmount},


	    TTL_DEALS=#{template.totalDeals},


	    TTL_DEALS=#{template.totalAccess}

    where id = #{template.id}
