mapper.select:
SELECT * FROM ( SELECT * FROM ( SELECT ROWNUM AS RN, R0.* FROM ( SELECT s.pk AS operateId, s.P_OPERATORBY AS operatorBy, "TO_CHAR" ( s.P_OPERATORDATE, 'yyyy-MM-dd hh24:mi:ss' ) AS operatorTime, s.P_OPERATORDESC AS operatorDesc, s.P_OPERATORBYNAME AS operatorByName, s.P_AGENCYCODE AS agencyCode, s.P_ACCEPTEDCODE AS storeName, sa.P_ORDERCODE AS salOrderCode FROM SALESLEADSOPERATION s LEFT JOIN SALESLEADSORDER sa ON sa.pk = s.P_SALESLEADSORDER WHERE 1 = 1
AND sa.P_ORDERCODE like '%${salOrderCode}%'
AND P_AGENCYCODE like '%${agencyCode}%'
AND "TO_CHAR" (s.P_OPERATORDATE,'yyyy-MM-dd' ) >= #{startTime}
AND "TO_CHAR" (s.P_OPERATORDATE,'yyyy-MM-dd' ) <= #{endTime}
ORDER BY s.P_OPERATORDATE DESC, s.P_SALESLEADSORDER
) R0 ) R1 WHERE R1.RN < ${pageNum} * ${pageSize} + 1 ) R2 WHERE R2.RN > (${pageNum} - 1) * ${pageSize}

SELECT count(1) as conut FROM SALESLEADSOPERATION s LEFT JOIN SALESLEADSORDER sa ON sa.pk = s.P_SALESLEADSORDER WHERE 1 = 1
AND sa.P_ORDERCODE like '%${salOrderCode}%'
AND P_AGENCYCODE like '%${agencyCode}%'
AND "TO_CHAR" (s.P_OPERATORDATE,'yyyy-MM-dd' ) >= #{startTime}
AND "TO_CHAR" (s.P_OPERATORDATE,'yyyy-MM-dd' ) <= #{endTime}

SELECT s.pk AS operateId, s.P_OPERATORBY AS operatorBy, "TO_CHAR" ( s.P_OPERATORDATE, 'yyyy-MM-dd hh24:mi:ss' ) AS operatorTime, s.P_OPERATORDESC AS operatorDesc, s.P_OPERATORBYNAME AS operatorByName, s.P_AGENCYCODE AS agencyCode, s.P_ACCEPTEDCODE AS storeName, sa.P_ORDERCODE AS salOrderCode FROM SALESLEADSOPERATION s LEFT JOIN SALESLEADSORDER sa ON sa.pk = s.P_SALESLEADSORDER WHERE 1 = 1
AND sa.P_ORDERCODE like '%${salOrderCode}%'
AND P_AGENCYCODE like '%${agencyCode}%'
AND "TO_CHAR" (s.P_OPERATORDATE,'yyyy-MM-dd' ) >= #{startTime}
AND "TO_CHAR" (s.P_OPERATORDATE,'yyyy-MM-dd' ) <= #{endTime}
ORDER BY s.P_OPERATORDATE DESC, s.P_SALESLEADSORDER

